export declare const blog = "\ndatasource db {\n  provider = \"postgresql\"\n  url      = \"postgresql://localhost:5432/db\"\n}\n\ngenerator client {\n  provider  = \"prisma-client-js\"\n  output    = \"@prisma/client\"\n  transpile = false\n}\n\n/// User model comment\nmodel User {\n  id    String  @default(uuid()) @id\n  email String  @unique\n  /// name comment\n  name  String?\n  posts Post[]\n}\n\nmodel Post {\n  id        String   @default(cuid()) @id\n  createdAt DateTime @default(now())\n  updatedAt DateTime @updatedAt\n  published Boolean\n  title     String\n  content   String?\n  author    User?\n}\n\nenum Role {\n  USER\n  ADMIN\n}\n";
